        [WorkItem(30140, "https://github.com/dotnet/roslyn/issues/30140")]
        [Fact]
        public void NullCoalescingAssignment_Contravariant()
        {
            var source = @"
class C
{
#nullable disable
    C GetC() => null;
#nullable enable
    void M(C? c1, C c2)
    {
        // nullable + non-null = non-null
#nullable disable
        C c3
#nullable enable
                  = (c1 ??= GetC());
        _ = c1/*T:C!*/;
        _ = c3/*T:C!*/;

        // oblivious + nullable = nullable
        // Since c3 is non-nullable, the result is non-nullable.
        c1 = null;
        var c4 = (c3 ??= c1);
        _ = c3/*T:C?*/;
        _ = c4/*T:C?*/;

        // oblivious + not nullable = not nullable
        c3 = GetC();
        var c5 = (c3 ??= c2);
        _ = c3/*T:C!*/;
        _ = c5/*T:C!*/;

        // not nullable + oblivious = not nullable
        var c6 = (c2 ??= GetC());
        _ = c2/*T:C!*/;
        _ = c6/*T:C!*/;
    }
}";

            var comp = CreateCompilation(source, options: WithNonNullTypesTrue());
            comp.VerifyDiagnostics();
            comp.VerifyTypes();
        }

