        [Fact, WorkItem(34299, "https://github.com/dotnet/roslyn/issues/34299")]
        public void CheckLambdaInLambdaInference()
        {
            var source =
@"using System;

class C
{
    static Func<T> M<T>(Func<T> f) => f;
    static void G(int i, object? o, string? s)
    {
        if (o == null) return;
        var f = M(() => o);
        var f2 = M(() => {
                if (i == 1) return f;
                if (i == 2) return () => s; // 1
                return () => {
                        return s; // 2
                    };
            });
        f2().ToString();
    }
}
";
