                static void M4([UnscopedRef] out S p1, ref S p2) {
                    p1 = default;
                    p2.refField = ref p1.field; // 5
                    p2.refField = ref p1.refField; // 6
                }

                static void M5(ref S p1, ref S2 p2) {
                    p2 = Inner1(ref p1); // 7
                    p2 = Inner2(ref p1); // Okay
