                    p2 = Inner1(ref p1); // 8
                    p2 = Inner2(ref p1); // Okay
                }

                static S2 M8(scoped ref S p) {
                    if (condition()) return Inner1(ref p); // 9
                    if (condition()) return Inner2(ref p); // Okay

