            Assert.False(cctor.IsAsync);
            Assert.False(cctor.IsOverride);
            Assert.Equal(Accessibility.Private, cctor.DeclaredAccessibility);
            Assert.Equal(MethodKind.StaticConstructor, cctor.MethodKind);
        }

